ALTER DATABASE db CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci;

CREATE TABLE account (
    id INT AUTO_INCREMENT PRIMARY KEY,
    gmail VARCHAR(100) COLLATE utf8mb4_unicode_ci NOT NULL UNIQUE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

ALTER TABLE account CONVERT TO CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci;

-- サンプルデータの挿入（gmailのみ）
INSERT INTO account (gmail) VALUES 
    ('test@gmail.com'),
    ('test1@gmail.com');

-- article テーブルの作成
CREATE TABLE article (
    id INT PRIMARY KEY AUTO_INCREMENT,
    title VARCHAR(255) NOT NULL,
    summary150 VARCHAR(200) NOT NULL,
    summary1000 VARCHAR(1000),
    content TEXT NOT NULL,
    url VARCHAR(255) NOT NULL,
    published_date DATE,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

ALTER TABLE article CONVERT TO CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci;

-- session テーブルの作成
CREATE TABLE session (
    session_id VARCHAR(64) NOT NULL UNIQUE
    user_id INT NOT NULL UNIQUE,
    date DATETIME NOT NULL
);

ALTER TABLE session CONVERT TO CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci;

-- survey テーブルの作成
CREATE TABLE survey (
    id INT AUTO_INCREMENT PRIMARY KEY,
    userid INT NOT NULL,
    age INT NOT NULL,
    gender ENUM('男', '女', 'その他') NOT NULL,
    job VARCHAR(100) NOT NULL,
    preferred_article_detail TEXT NOT NULL,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    CONSTRAINT fk_survey_account FOREIGN KEY (userid) REFERENCES account(id)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

ALTER TABLE survey CONVERT TO CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci;

-- サンプルデータの挿入（useridはaccountのidに合わせる）
INSERT INTO survey (userid, age, gender, job, preferred_article_detail) VALUES
    (1, 25, '男', 'エンジニア', '技術系の記事をもっと読みたい。特にAI関連に興味がある。'),
    (2, 32, '女', 'マーケティング', 'マーケティング戦略やSNSの最新トレンドについて知りたい。');

-- read_log テーブルの作成
CREATE TABLE read_log (
    id INT AUTO_INCREMENT PRIMARY KEY,
    user_id INT NOT NULL,
    article_id INT NOT NULL,
    read_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    CONSTRAINT fk_read_log_article FOREIGN KEY (article_id) REFERENCES article(id)
);

-- source_url テーブル（元となるURLを一意に管理）
CREATE TABLE source_url (
  id INT AUTO_INCREMENT PRIMARY KEY,
  url VARCHAR(255) NOT NULL UNIQUE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

ALTER TABLE source_url CONVERT TO CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci;

INSERT INTO source_url (url) VALUES
    ('https://www.gizmodo.jp'),
    ('https://qiita.com'),
    ('https://www3.nhk.or.jp/news');

CREATE TABLE favorite_sites (
  id INT AUTO_INCREMENT PRIMARY KEY,
  user_id INT NOT NULL,
  source_id INT NOT NULL,
  CONSTRAINT fk_favorite_sites_account FOREIGN KEY (user_id) REFERENCES account(id),
  CONSTRAINT fk_favorite_sites_source_url FOREIGN KEY (source_id) REFERENCES source_url(id)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

ALTER TABLE favorite_sites CONVERT TO CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci;

INSERT INTO favorite_sites (user_id, source_id) VALUES
    (1, 1),
    (1, 2),
    (2, 3);

-- retrieved_urls テーブル（取得済みURLの記録）
CREATE TABLE retrieved_urls (
  id INT AUTO_INCREMENT PRIMARY KEY,
  source_id INT NOT NULL,
  retrieved_url VARCHAR(255) NOT NULL,
  retrieved_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
  CONSTRAINT fk_retrieved_urls_source_url FOREIGN KEY (source_id) REFERENCES source_url(id)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

ALTER TABLE favorite_sites CONVERT TO CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci;

-- サンプルデータの挿入
INSERT INTO `article` VALUES (1,'新年の目標達成の秘訣','新年の目標達成法を紹介','新年に立てる目標の達成方法を解説します 。年初に意気込んで目標を立てたものの、途中で挫折してしまう経験はありませんか？目標を達成するためには、具体的な計画を立てることが重要です。目標を小さなステップに分けて実行し、進捗を確認しながら軌道修正を行うことで、無理なく目標に近づけます。また、モチベーションを維持するためには、達成した際の自分を想像したり、定期的に自己評価を行うことが効果的です。さらに、目標を周囲に宣言することで、自分にプレッシャーをかけるのも一つの方法です。日々の小さな努力を積み重ねることが、最終的な成功へと繋がります。2025年を素晴らしい一年にするために、目標を達成するための具体的なアクションプランを立てましょう。','新年 の目標達成には、まず具体的な計画を立てることが重要です。特に、目標を小さなステップに分けて、達成感を味わいながら進めることがポイントです。また、モチベーションを維持するためには、定期的に自己評価を行い、進捗を確認しましょう。...','https://example.com/article1','2025-01-01','2025-02-25 06:17:23'),(2,'最新のテクノロジートレンド2025','2025年の注目技術を紹介','2025年に注目すべきテクノロジートレンドを詳しく解説します。今年はAIの進化がさらに加速し、IoTとの連携が強化される見込みです 。特に、AIを活用した自動化技術がビジネスの生産性を飛躍的に向上させることが期待されています。また、ブロックチェーン技術の進化により、セキュリティの強化や取引の透明性が向上し、金融業界だけでなく多くの産業に変革をもたらしています。さらに、メタバースの普及に伴い、VR（バーチャルリアリティ）とAR（拡張現実）が日常生活に浸透しつつあります。これらの技術はエンターテインメントだけでなく、教育や医療の現場でも活用が進むでしょう。2025年は、これらのテクノロジーが生活をより便利に、より豊かに変えていく年になることが予想されます。','AIの進化が加速し、IoTとの連携が強化されています。自動化技術によりビジネスの生産性が飛躍的に向上し、ブロックチェーン技術は金融業界を中心に多くの産業に影響を与えています。...','https://example.com/article2','2025-02-15','2025-02-25 06:17:23'),(3,'ついに花粉がやってきた！ 少しでも快適に過ごすための裏ワザ','花粉の季節が到来し、快適に過ごすための裏ワザを紹介します。花粉症対策や日常生活での工夫を通じて、少しでも快適に過ごす方法を提案します。',NULL,'ギズモード・ジャパン | 日本最大級のガジェット＆テクノロジーサイト\nグローバルナビゲーションへジャンプ\nメインコ ンテンツへジャンプ\nフッターへジャンプ\nこのページではJavaScriptを使用しています。JavaScriptをONにしてください。\nギズモード・ジャパン\nSNS\nMENU\nCATEGORY\nジャンル・分野\nTECHNOLOGY\nSCIENCE\nPRODUCT\nENTERTAINMENT\nLIFESTYLE\nDESIGN\nBUSINESS\nNEWS\nLATEST\n最新記事\nREVIEWS\n商品レビュー\nSPECIAL\n特集記事\nTAG\nタグ一覧\nAI\nAI関連の最新記事\nSALE\nAmazonおすすめ\nGIZMODOについて\nスタッフ一覧\n広告掲載について\n媒体資料のお問い合わせ\n運営会社\n利用規約\nプライバシーポ リシー\nメールマガジン利用規約\nインフォマティブデータポリシー\nスポンサード＆アフィリエイト表記について\nCopyright © Mediagene Inc. All Rights Reserved.\nTwitch、保存できるアーカイブ動画の100時間制限を発動。4月から\n2025.02.25\nFEATURE\n｢iPhone 16e｣に買い替えて満足度が高いiPhone、このへんです\n2025.02.21\n22,357\nさよならiPhone SE。さよならTouch ID\n2025.02.20\n177,815\n悩ましい端末、iPhone 16e。購入前に比較すべきポイントまとめました\n2025.02.20\n13,005\n“鍵盤”恐怖症でも作曲OK。『Chordcat』でコード進行の闇から解放されたよ\n2025.02.2